var Database = require('better-sqlite3');
var db = new Database('public/db/pal1.db');

exports.myUpd=function(uni,usr,sku){
var stm=db.prepare('update tmp set uni=? where usr=? and sku=? ');

try{
stm.run(uni,usr,sku);
    
}catch(err){console.log(err);}


};

